using { /Fortnite.com/Devices }
using { /Verse.org/Simulation }
using { /UnrealEngine.com/Temporary/Diagnostics }

SlowMotion := class(creative_device):
    @editable var Class : int = 0
    @editable ClassTracker : tracker_device = tracker_device{}
    @editable HUDablity : hud_message_device = hud_message_device{}

    @editable SlowMotionSequence : cinematic_sequence_device = cinematic_sequence_device{}
    @editable SlowMoLength : float = 0.0

    @editable RemoteManager : signal_remote_manager_device = signal_remote_manager_device{}
    @editable ItemGranter:item_granter_device = item_granter_device{}

    @editable Superpowerused : accolades_device = accolades_device{}


    OnBegin<override>()<suspends>:void=
        RemoteManager.PrimarySignalEvent.Subscribe(StartSlowMoSleep)
        RemoteManager.SecondarySignalEvent.Subscribe(StartSlowMoSleep)
        InitSpawners()

    InitSpawners() : void=
        Spawners := FindCreativeObjectsWithTag(spawner{})
        for (Obj : Spawners):
            if (Spawner := player_spawner_device[Obj]):
                Spawner.SpawnedEvent.Subscribe(OnPlayerSpawned)

    OnPlayerSpawned(Agent : agent): void=
        if (Player := player[Agent]):
            spawn{GiveItem(Agent)}

    GiveItem(Agent : agent)<suspends>:void=
        Sleep(1.0)
        if(ClassTracker.GetValue(Agent) = Class):
            ItemGranter.GrantItem(Agent)
            HUDablity.Show(Agent)

    StartSlowMoSleep(Agent : agent):void=
        if(ClassTracker.GetValue(Agent) = Class):
            spawn{StartSlowMo(Agent)}

    StartSlowMo(Agent:agent) <suspends>: void=  
        if(ClassTracker.GetValue(Agent) = Class):
            SlowMotionSequence.SetPlaybackTime(0.0)
            SlowMotionSequence.Play()
            Superpowerused.Award(Agent)
            Sleep(SlowMoLength)
            SlowMotionSequence.GoToEndAndStop()
